<?php 
function getIP(){ $ipaddress = 'UNKNOWN'; if (isset($_SERVER['HTTP_CLIENT_IP']) && $_SERVER['HTTP_CLIENT_IP']!="") $ipaddress = $_SERVER['HTTP_CLIENT_IP'];elseif(isset($_SERVER['HTTP_X_FORWARDED_FOR']) &&  $_SERVER['HTTP_X_FORWARDED_FOR']!="") $ipaddress = $_SERVER['HTTP_X_FORWARDED_FOR'];elseif(isset($_SERVER['HTTP_X_FORWARDED']) &&  $_SERVER['HTTP_X_FORWARDED']!="") $ipaddress = $_SERVER['HTTP_X_FORWARDED'];elseif(isset($_SERVER['HTTP_FORWARDED_FOR']) &&  $_SERVER['HTTP_FORWARDED_FOR']!="") $ipaddress = $_SERVER['HTTP_FORWARDED_FOR'];elseif(isset($_SERVER['HTTP_FORWARDED']) &&  $_SERVER['HTTP_FORWARDED']!="") $ipaddress = $_SERVER['HTTP_FORWARDED'];elseif(isset($_SERVER['REMOTE_ADDR']) &&  $_SERVER['REMOTE_ADDR']!="") $ipaddress = $_SERVER['REMOTE_ADDR']; return $ipaddress; }